import React, { Component } from "react";

import indexStyles from "./assets/indexStyles.js";

import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import ListItemSecondaryAction from "@material-ui/core/ListItemSecondaryAction";
import DeleteIcon from "@material-ui/icons/Delete";
import IconButton from "@material-ui/core/IconButton";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";

class App extends Component {
  state = {
    data: [],
    title: "",
    addMenuOpened: false
  };

  componentDidMount() {
    this.callApi()
      .then(res => this.setState({ data: res }))
      .catch(err => console.log(err));
  }

  callApi = async () => {
    const data = await fetch("/api/mensagem");
    const body = await data.json();
    if (data.status !== 200) throw Error(body.message);

    return body;
  };

  deleteFunction = id => {
    this.setState({
      data: this.state.data.filter(function(value, index, arr) {
        return index != id;
      })
    });
  };

  showMenu = () => {
    const { addMenuOpened } = this.state;
    this.setState({ addMenuOpened: !addMenuOpened });
    console.log(this.state.addMenuOpened);
  };

  addItem = () => {
    const { data, addMenuOpened } = this.state;
    const temp = {
      title: this.state.title,
      price: this.state.price,
      description: this.state.desc
    };

    addMenuOpened = false;
    data.push(temp);
  };

  handleTitleChange = e => {
    this.setState({
      title: e.target.value
    });
    console.log(this.state.title);
  };
  handlePriceChange = e => {
    this.setState({
      price: e.target.value
    });
    console.log(this.state.textPrice);
  };
  handleDescChange = e => {
    this.setState({
      desc: e.target.value
    });
    console.log(this.state.textDesc);
  };

  render() {
    const data = this.state.data;

    return (
      <div
        className="App"
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          height: "100vh"
        }}
      >
        <div>
          <h1>Cardápio</h1>
          <List
            style={{
              display: "flex",
              flexDirection: "column",
              alignItems: "flex-start",
              width: "55vh"
            }}
          >
            {data != []
              ? data.map((value, id) => {
                  return (
                    <ListItem
                      style={{
                        display: "flex",
                        justifyContent: "space-between",
                        width: "55vh"
                      }}
                    >
                      <ListItemText
                        key={id}
                        primary={value.title + ` - ` + value.price}
                        secondary={value.description}
                      />
                      <ListItemSecondaryAction>
                        <IconButton edge="end" aria-label="delete">
                          <DeleteIcon
                            onClick={this.deleteFunction.bind(this, id)}
                          />
                        </IconButton>
                      </ListItemSecondaryAction>
                    </ListItem>
                  );
                })
              : "vazio"}
          </List>
        </div>
        <Button
          variant="contained"
          color="primary"
          style={{ margin: "0 16px 0 16px" }}
          onClick={this.showMenu}
        >
          Novo Item
        </Button>
        <div
          style={
            this.state.addMenuOpened
              ? indexStyles.addMenuOpen
              : indexStyles.addMenuHidden
          }
        >
          <TextField
            variant="outlined"
            label="Título"
            value={this.state.title}
            onChange={this.handleTitleChange}
            style={indexStyles.textField}
          />
          <TextField
            variant="outlined"
            label="Preço"
            value={this.state.price}
            onChange={this.handlePriceChange}
            style={indexStyles.textField}
          />
          <TextField
            variant="outlined"
            label="Descrição"
            value={this.state.desc}
            onChange={this.handleDescChange}
            multiline
            style={indexStyles.textField}
          />
          <Button variant="contained" color="primary" onClick={this.addItem}>
            Adicionar
          </Button>
        </div>
      </div>
    );
  }
}

export default App;
