import React, { Component } from "react";

import indexStyles from "./assets/indexStyles.js";

import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import ListItemSecondaryAction from "@material-ui/core/ListItemSecondaryAction";
import DeleteIcon from "@material-ui/icons/Delete";
import IconButton from "@material-ui/core/IconButton";
import Button from "@material-ui/core/Button";

class App extends Component {
  state = {
    response: [],
    addMenuOpened: false
  };

  componentDidMount() {
    this.callApi()
      .then(res => this.setState({ response: res }))
      .catch(err => console.log(err));
  }

  callApi = async () => {
    const response = await fetch("/api/mensagem");
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);

    return body;
  };

  deleteFunction = id => {
    this.setState({
      response: this.state.response.filter(function(value, index, arr) {
        return index != id;
      })
    });
  };

  showMenu = () => {
    const showMenu = this.state;
    this.setState({ showMenu: !showMenu });
  };

  render() {
    const data = this.state.response;

    return (
      <div
        className="App"
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          height: "100vh"
        }}
      >
        <div>
          <h1>CardÃ¡pio</h1>
          <List
            style={{
              display: "flex",
              flexDirection: "column",
              alignItems: "flex-start",
              width: "55vh"
            }}
          >
            {data != []
              ? data.map((value, id) => {
                  return (
                    <ListItem
                      style={{
                        display: "flex",
                        justifyContent: "space-between",
                        width: "55vh"
                      }}
                    >
                      <ListItemText
                        key={id}
                        primary={value.title + ` - ` + value.price}
                        secondary={value.description}
                      />
                      <ListItemSecondaryAction>
                        <IconButton edge="end" aria-label="delete">
                          <DeleteIcon
                            onClick={this.deleteFunction.bind(this, id)}
                          />
                        </IconButton>
                      </ListItemSecondaryAction>
                    </ListItem>
                  );
                })
              : "vazio"}
          </List>
        </div>
        <Button
          variant="contained"
          color="primary"
          style={{ marginLeft: "16px " }}
          onClick={openMenu}
        >
          Novo Item
        </Button>
        <div
          style={
            this.state.addMenuOpened
              ? indexStyles.addMenuOpen
              : indexStyles.addMenuHidden
          }
        ></div>
      </div>
    );
  }
}

export default App;
